   275→/// Detected regression
   276→#[derive(Debug, Clone, Serialize, Deserialize)]
   277→pub struct Regression {
   278→    pub dimension: MetricDimension,
   279→    pub baseline_value: f64,
   280→    pub current_value: f64,
   281→    pub percent_change: f64,
   282→    pub severity: RegressionSeverity,
   283→}
   284→
   285→#[derive(Debug, Clone, Copy, PartialEq, Eq, Serialize, Deserialize)]
   286→pub enum RegressionSeverity {
   287→    Medium,
   288→    High,
   289→    Critical,
   290→}
