   292→/// Calculate percentile from sorted values
   293→fn percentile(sorted: &[f64], p: f64) -> f64 {
   294→    if sorted.is_empty() {
   295→        return 0.0;
   296→    }
   297→
   298→    let idx = (p * (sorted.len() - 1) as f64).round() as usize;
   299→    sorted[idx.min(sorted.len() - 1)]
   300→}
