   302→/// Weekly baseline report generator
   303→pub struct BaselineReportGenerator<'a> {
   304→    db: &'a MetricsDb,
   305→}
   306→
   307→impl<'a> BaselineReportGenerator<'a> {
   308→    pub fn new(db: &'a MetricsDb) -> Self {
   309→        Self { db }
   310→    }
   311→
   312→    /// Generate a markdown report of current baselines
   313→    pub fn generate_report(&self) -> Result<String> {
   314→        let mut report = String::new();
   315→
   316→        report.push_str("# Autopilot Baseline Metrics\n\n");
   317→        report.push_str(&format!("Generated: {}\n\n", Utc::now().format("%Y-%m-%d %H:%M UTC")));
   318→
   319→        report.push_str("## Current Baselines\n\n");
   320→        report.push_str("| Dimension | Mean | StdDev | p50 | p90 | p99 | Samples |\n");
   321→        report.push_str("|-----------|------|--------|-----|-----|-----|--------|\n");
   322→
   323→        for dimension in MetricDimension::all() {
   324→            if let Some(baseline) = self.db.get_baseline(dimension.as_str())? {
   325→                report.push_str(&format!(
   326→                    "| {} | {:.4} | {:.4} | {:.4} | {:.4} | {:.4} | {} |\n",
   327→                    dimension.as_str(),
   328→                    baseline.mean,
   329→                    baseline.stddev,
   330→                    baseline.p50,
   331→                    baseline.p90,
   332→                    baseline.p99,
   333→                    baseline.sample_count
   334→                ));
   335→            }
   336→        }
   337→
   338→        report.push_str("\n## Interpretation\n\n");
   339→        report.push_str("- **tool_error_rate**: Lower is better (target: <0.05 or 5%)\n");
   340→        report.push_str("- **completion_rate**: Higher is better (target: >0.95 or 95%)\n");
   341→        report.push_str("- **avg_duration**: Depends on task complexity\n");
   342→        report.push_str("- **avg_cost**: Lower is better (efficiency)\n");
   343→        report.push_str("- **avg_tokens**: Stable is better (consistency)\n");
   344→        report.push_str("- **cache_hit_rate**: Higher is better (target: >0.90 or 90%)\n");
   345→
   346→        Ok(report)
   347→    }
   348→}
