     1→//! WebSocket support for real-time Nostr event streaming
     2→
     3→use actix_web::{web, HttpRequest, HttpResponse};
     4→use actix_ws::Message;
     5→use futures_util::StreamExt;
     6→use std::sync::Arc;
     7→use tokio::sync::{broadcast, Mutex};
     8→use tokio::task::JoinHandle;
     9→use tracing::warn;
    10→
    11→/// Broadcasts messages to all connected WebSocket clients
    12→pub struct WsBroadcaster {
    13→    tx: broadcast::Sender<String>,
    14→    /// Active WebSocket task handles for graceful shutdown
    15→    tasks: Mutex<Vec<JoinHandle<()>>>,
    16→}
    17→
    18→impl WsBroadcaster {
    19→    pub fn new(capacity: usize) -> Self {
    20→        let (tx, _rx) = broadcast::channel(capacity);
    21→        Self {
    22→            tx,
    23→            tasks: Mutex::new(Vec::new()),
    24→        }
    25→    }
    26→
    27→    /// Broadcast a message to all connected clients
    28→    /// Returns the number of receivers that received the message
    29→    #[allow(dead_code)]
    30→    pub fn broadcast(&self, msg: &str) -> usize {
    31→        match self.tx.send(msg.to_string()) {
    32→            Ok(receiver_count) => receiver_count,
    33→            Err(e) => {
    34→                // This happens when there are no active receivers
    35→                // Not an error condition - just means no clients connected
    36→                warn!("Broadcast send failed (no receivers): {}", e);
    37→                0
    38→            }
    39→        }
    40→    }
    41→
    42→    /// Subscribe to broadcasts
    43→    pub fn subscribe(&self) -> broadcast::Receiver<String> {
    44→        self.tx.subscribe()
    45→    }
    46→
    47→    /// Store a WebSocket task handle
    48→    async fn add_task(&self, handle: JoinHandle<()>) {
    49→        let mut tasks = self.tasks.lock().await;
    50→        tasks.push(handle);
    51→    }
    52→
    53→    /// Clean up completed tasks
    54→    async fn cleanup_tasks(&self) {
    55→        let mut tasks = self.tasks.lock().await;
    56→        tasks.retain(|task| !task.is_finished());
    57→    }
    58→
    59→    /// Shutdown all WebSocket tasks gracefully
    60→    #[allow(dead_code)]
    61→    pub async fn shutdown(&self) {
    62→        let mut tasks = self.tasks.lock().await;
    63→        for task in tasks.drain(..) {
    64→            task.abort();
    65→            let _ = task.await;
    66→        }
    67→    }
    68→}
    69→
    70→/// WebSocket handler
    71→pub async fn ws_handler(
    72→    req: HttpRequest,
    73→    stream: web::Payload,
    74→    broadcaster: Arc<WsBroadcaster>,
    75→) -> Result<HttpResponse, actix_web::Error> {
    76→    let (res, mut session, mut msg_stream) = actix_ws::handle(&req, stream)?;
    77→
    78→    let mut rx = broadcaster.subscribe();
    79→
    80→    // Clean up completed tasks periodically
    81→    broadcaster.cleanup_tasks().await;
    82→
    83→    // Spawn task to handle this WebSocket connection and store the handle
    84→    let handle = actix_web::rt::spawn(async move {
    85→        loop {
    86→            tokio::select! {
    87→                // Receive messages from client
    88→                Some(Ok(msg)) = msg_stream.next() => {
    89→                    match msg {
    90→                        Message::Ping(bytes) => {
    91→                            let _ = session.pong(&bytes).await;
    92→                        }
    93→                        Message::Close(_) => break,
    94→                        _ => {}
    95→                    }
    96→                }
    97→                // Receive broadcasts
    98→                Ok(broadcast_msg) = rx.recv() => {
    99→                    let _ = session.text(broadcast_msg).await;
   100→                }
   101→                else => break,
   102→            }
   103→        }
   104→    });
   105→
   106→    broadcaster.add_task(handle).await;
   107→
   108→    Ok(res)
   109→}
   110→

