     1→//! Nostr client for connecting to relays and subscribing to git events
     2→
     3→use anyhow::Result;
     4→use nostr::Event;
     5→use nostr_client::{PoolConfig, RelayPool};
     6→use serde_json::json;
     7→use std::path::PathBuf;
     8→use std::sync::Arc;
     9→use tokio::sync::{mpsc, Mutex};
    10→use tokio::task::JoinHandle;
    11→use tracing::{debug, error, info};
    12→
    13→use crate::nostr::cache::EventCache;
    14→use crate::nostr::publish_result::{ErrorCategory, PublishResult, RelayFailure};
    15→use crate::nostr::retry::{retry_with_backoff, RetryConfig};
    16→use crate::ws::WsBroadcaster;
    17→
    18→/// NIP-34 event kinds for git operations
    19→pub mod kinds {
    20→    pub const REPOSITORY_ANNOUNCEMENT: u16 = 30617;
    21→    pub const REPOSITORY_STATE: u16 = 30618;
    22→    pub const PATCH: u16 = 1617;
    23→    pub const PULL_REQUEST: u16 = 1618;
    24→    pub const PR_UPDATE: u16 = 1619;
    25→    pub const ISSUE: u16 = 1621;
    26→    pub const STATUS_OPEN: u16 = 1630;
    27→    pub const STATUS_APPLIED: u16 = 1631;
    28→    pub const STATUS_CLOSED: u16 = 1632;
    29→    pub const STATUS_DRAFT: u16 = 1633;
    30→
    31→    // NIP-34 extensions for agent workflows
    32→    pub const ISSUE_CLAIM: u16 = 1634;
    33→    pub const WORK_ASSIGNMENT: u16 = 1635;
    34→    pub const BOUNTY_OFFER: u16 = 1636;
    35→    pub const BOUNTY_CLAIM: u16 = 1637;
    36→}
    37→
    38→/// Nostr client for GitAfter
    39→pub struct NostrClient {
    40→    pool: Arc<RelayPool>,
    41→    broadcaster: Arc<WsBroadcaster>,
    42→    cache: Arc<Mutex<EventCache>>,
    43→    retry_config: RetryConfig,
    44→    /// Event handler task handle for graceful shutdown
    45→    event_handler: Mutex<Option<JoinHandle<()>>>,
    46→}
    47→
    48→impl NostrClient {
    49→    /// Create a new Nostr client with relay URLs
    50→    pub fn new(_relay_urls: Vec<String>, broadcaster: Arc<WsBroadcaster>) -> Result<Self> {
    51→        let config = PoolConfig::default();
    52→        let pool = Arc::new(RelayPool::new(config));
    53→
    54→        // Initialize cache in user's data directory
    55→        let cache_dir = dirs::data_local_dir()
    56→            .unwrap_or_else(|| PathBuf::from("."))
    57→            .join("gitafter");
    58→        let cache_path = cache_dir.join("events.db");
    59→        let cache = Arc::new(Mutex::new(EventCache::new(cache_path)?));
    60→
    61→        Ok(Self {
    62→            pool,
    63→            broadcaster,
    64→            cache,
    65→            retry_config: RetryConfig::default(),
    66→            event_handler: Mutex::new(None),
    67→        })
    68→    }
    69→
    70→    /// Create a new Nostr client with custom retry configuration
    71→    #[allow(dead_code)]
    72→    pub fn with_retry_config(
    73→        _relay_urls: Vec<String>,
    74→        broadcaster: Arc<WsBroadcaster>,
    75→        retry_config: RetryConfig,
    76→    ) -> Result<Self> {
    77→        let config = PoolConfig::default();
    78→        let pool = Arc::new(RelayPool::new(config));
    79→
    80→        let cache_dir = dirs::data_local_dir()
    81→            .unwrap_or_else(|| PathBuf::from("."))
    82→            .join("gitafter");
    83→        let cache_path = cache_dir.join("events.db");
    84→        let cache = Arc::new(Mutex::new(EventCache::new(cache_path)?));
    85→
    86→        Ok(Self {
    87→            pool,
    88→            broadcaster,
    89→            cache,
    90→            retry_config,
    91→            event_handler: Mutex::new(None),
    92→        })
    93→    }
    94→
    95→    /// Connect to all configured relays
    96→    pub async fn connect(&self, relay_urls: Vec<String>) -> Result<()> {
    97→        info!("Connecting to {} relays...", relay_urls.len());
    98→
    99→        // Add relays to pool
   100→        for url in &relay_urls {
   101→            if let Err(e) = self.pool.add_relay(url).await {
   102→                error!("Failed to add relay {}: {}", url, e);
   103→            }
   104→        }
   105→
   106→        // Connect to all relays
   107→        self.pool.connect_all().await?;
   108→
   109→        info!("Successfully connected to relays");
   110→        Ok(())
   111→    }
   112→
   113→    /// Subscribe to NIP-34 git events
   114→    pub async fn subscribe_to_git_events(&self) -> Result<()> {
   115→        info!("Subscribing to NIP-34 git events...");
   116→
   117→        // Create filter for NIP-34 git events and trajectory events
   118→        let filters = vec![json!({
   119→            "kinds": [
   120→                1,     // Text notes (for PR/issue review comments via NIP-22)
   121→                1985,  // Labels (NIP-32, for agent reputation)
   122→                kinds::REPOSITORY_ANNOUNCEMENT,
   123→                kinds::REPOSITORY_STATE,
   124→                kinds::PATCH,
   125→                kinds::PULL_REQUEST,
   126→                kinds::PR_UPDATE,
   127→                kinds::ISSUE,
   128→                kinds::STATUS_OPEN,
   129→                kinds::STATUS_APPLIED,
   130→                kinds::STATUS_CLOSED,
   131→                kinds::STATUS_DRAFT,
   132→                kinds::ISSUE_CLAIM,
   133→                kinds::WORK_ASSIGNMENT,
   134→                kinds::BOUNTY_OFFER,
   135→                kinds::BOUNTY_CLAIM,
   136→                38030, // Trajectory Session
   137→                38031, // Trajectory Event
   138→            ],
   139→            "limit": 100
   140→        })];
   141→
   142→        // Subscribe and get event receiver
   143→        let mut event_rx = self
   144→            .pool
   145→            .subscribe("gitafter-main", &filters)
   146→            .await?;
   147→
   148→        info!("Successfully subscribed to git events");
   149→
   150→        // Spawn task to handle incoming events and store the handle
   151→        let broadcaster = Arc::clone(&self.broadcaster);
   152→        let cache = Arc::clone(&self.cache);
   153→        let handle = tokio::spawn(async move {
   154→            while let Some(event) = event_rx.recv().await {
   155→                debug!("Received event: kind={} id={}", event.kind, event.id);
   156→
   157→                // Cache the event
   158→                if let Err(e) = cache.lock().await.insert_event(&event) {
   159→                    error!("Failed to cache event: {}", e);
   160→                }
   161→
   162→                // Create notifications for relevant events
