   450→    /// Publish a signed event to all connected relays with retry and error handling
   451→    #[allow(dead_code)]
   452→    pub async fn publish_event(&self, event: Event) -> Result<PublishResult> {
   453→        info!("Publishing event: kind={} id={}", event.kind, event.id);
   454→        let event_id = event.id.clone();
   455→
   456→        // Store in local cache first (best effort)
   457→        if let Err(e) = self.cache.lock().await.insert_event(&event) {
   458→            error!("Failed to cache event: {}", e);
   459→        }
   460→
   461→        // Publish to relay pool with retry
   462→        let pool = Arc::clone(&self.pool);
   463→        let event_clone = event.clone();
   464→
   465→        let publish_result = retry_with_backoff(
   466→            &self.retry_config,
   467→            "relay publish",
   468→            || {
   469→                let pool = Arc::clone(&pool);
   470→                let event = event_clone.clone();
   471→                async move {
   472→                    // Add timeout to prevent indefinite hanging
   473→                    tokio::time::timeout(
   474→                        std::time::Duration::from_secs(10),
   475→                        pool.publish(&event)
   476→                    )
   477→                    .await
   478→                    .map_err(|_| anyhow::anyhow!("Publish timeout"))
   479→                    .and_then(|r| r.map_err(|e| anyhow::anyhow!("Publish failed: {}", e)))
   480→                }
   481→            },
   482→        )
   483→        .await;
   484→
   485→        // Build detailed result
   486→        let result = match publish_result {
   487→            Ok(confirmations) => {
   488→                let confirmation_count = confirmations.len();
   489→                debug!("Received {} publish confirmations", confirmation_count);
   490→
   491→                // Broadcast to UI for real-time updates
   492→                if let Ok(json) = serde_json::to_string(&event) {
   493→                    self.broadcaster.broadcast(&format!(
   494→                        r#"<div class="event event-published" data-kind="{}">{}</div>"#,
   495→                        event.kind, json
   496→                    ));
   497→                }
   498→
   499→                info!(
   500→                    "Successfully published event: {} to {} relays",
   501→                    event_id, confirmation_count
   502→                );
   503→
   504→                PublishResult::success(event_id, confirmation_count, confirmation_count)
   505→            }
   506→            Err(e) => {
   507→                error!("Failed to publish event {}: {}", event_id, e);
   508→
   509→                // Create detailed failure result
   510→                let error_msg = e.to_string();
   511→                let category = ErrorCategory::from_error_message(&error_msg);
   512→
   513→                let failure = RelayFailure {
   514→                    relay_url: "pool".to_string(),
   515→                    error: error_msg.clone(),
   516→                    category,
   517→                };
   518→
   519→                // Broadcast failure to UI
   520→                self.broadcaster.broadcast(&format!(
   521→                    r#"<div class="event event-failed" data-kind="{}" data-error="{}">Failed to publish event</div>"#,
   522→                    event.kind,
   523→                    html_escape::encode_text(&error_msg)
   524→                ));
   525→
   526→                PublishResult::failure(event_id, 0, 1, vec![failure])
   527→            }
   528→        };
   529→
   530→        Ok(result)
   531→    }
