   580→    /// Handle notification triggers for incoming events
   581→    async fn handle_notification_triggers(
   582→        cache: &Arc<Mutex<EventCache>>,
   583→        event: &Event,
   584→    ) -> Result<()> {
   585→        let cache_lock = cache.lock().await;
   586→
   587→        match event.kind {
   588→            // Review comment on PR (kind:1 with 'e' tag referencing PR)
   589→            1 => {
   590→                // Find PR being commented on via 'e' tag
   591→                for tag in &event.tags {
   592→                    if tag.len() >= 2 && tag[0] == "e" {
   593→                        let pr_event_id = &tag[1];
   594→
   595→                        // Get the PR event to find its author
   596→                        if let Ok(Some(pr_event)) = cache_lock.get_event(pr_event_id) {
   597→                            if pr_event.kind == kinds::PULL_REQUEST {
   598→                                // Don't notify if user is commenting on their own PR
   599→                                if pr_event.pubkey != event.pubkey {
   600→                                    let title = "New review on your PR".to_string();
   601→                                    let preview = if event.content.len() > 100 {
   602→                                        Some(format!("{}...", &event.content[..100]))
   603→                                    } else {
   604→                                        Some(event.content.clone())
   605→                                    };
   606→
   607→                                    cache_lock.create_notification(
   608→                                        &pr_event.pubkey,
   609→                                        &event.id,
   610→                                        event.kind,
   611→                                        "pr_review",
   612→                                        &title,
   613→                                        preview.as_deref(),
   614→                                    )?;
   615→                                }
   616→                                break;
   617→                            }
   618→                        }
   619→                    }
   620→                }
   621→            }
   622→            // Status change events (approve, merge, close, draft)
   623→            kinds::STATUS_OPEN | kinds::STATUS_APPLIED | kinds::STATUS_CLOSED | kinds::STATUS_DRAFT => {
   624→                for tag in &event.tags {
   625→                    if tag.len() >= 2 && tag[0] == "e" {
   626→                        let pr_event_id = &tag[1];
   627→
   628→                        if let Ok(Some(pr_event)) = cache_lock.get_event(pr_event_id) {
   629→                            if pr_event.kind == kinds::PULL_REQUEST && pr_event.pubkey != event.pubkey {
   630→                                let status_name = match event.kind {
   631→                                    kinds::STATUS_OPEN => "opened",
   632→                                    kinds::STATUS_APPLIED => "merged",
   633→                                    kinds::STATUS_CLOSED => "closed",
   634→                                    kinds::STATUS_DRAFT => "marked as draft",
   635→                                    _ => "updated",
   636→                                };
   637→
   638→                                let title = format!("Your PR was {}", status_name);
   639→
   640→                                cache_lock.create_notification(
   641→                                    &pr_event.pubkey,
   642→                                    &event.id,
   643→                                    event.kind,
   644→                                    "pr_status",
   645→                                    &title,
   646→                                    None,
   647→                                )?;
   648→                                break;
   649→                            }
   650→                        }
   651→                    }
   652→                }
   653→            }
   654→            // Issue claim (kind:1634)
   655→            kinds::ISSUE_CLAIM => {
   656→                for tag in &event.tags {
   657→                    if tag.len() >= 2 && tag[0] == "e" {
   658→                        let issue_event_id = &tag[1];
   659→
   660→                        if let Ok(Some(issue_event)) = cache_lock.get_event(issue_event_id) {
   661→                            if issue_event.kind == kinds::ISSUE && issue_event.pubkey != event.pubkey {
   662→                                let title = "Someone claimed your issue".to_string();
   663→
   664→                                cache_lock.create_notification(
   665→                                    &issue_event.pubkey,
   666→                                    &event.id,
   667→                                    event.kind,
   668→                                    "issue_claim",
   669→                                    &title,
   670→                                    None,
   671→                                )?;
   672→                                break;
   673→                            }
   674→                        }
   675→                    }
   676→                }
   677→            }
   678→            _ => {}
   679→        }
   680→
   681→        Ok(())
   682→    }
   683→}
