    13→pub use publish_status::{publish_status_notification, publish_status_styles};
    14→
    15→/// Helper function to extract tag value from event
    16→pub fn get_tag_value(event: &Event, tag_name: &str) -> Option<String> {
    17→    event.tags.iter()
    18→        .find(|tag| tag.first().map(|t| t == tag_name).unwrap_or(false))
    19→        .and_then(|tag| tag.get(1).cloned())
    20→}
    21→
    22→/// Helper function to extract all values for a tag name
    23→fn get_all_tag_values(event: &Event, tag_name: &str) -> Vec<String> {
    24→    event.tags.iter()
    25→        .filter(|tag| tag.first().map(|t| t == tag_name).unwrap_or(false))
    26→        .filter_map(|tag| tag.get(1).cloned())
    27→        .collect()
    28→}
    29→
    30→/// Format a Unix timestamp as relative time (e.g., "2 hours ago", "yesterday")
    31→fn format_relative_time(timestamp: u64) -> String {
    32→    let dt = DateTime::from_timestamp(timestamp as i64, 0)
    33→        .unwrap_or_else(|| Utc::now());
    34→    let now = Utc::now();
    35→    let duration = now.signed_duration_since(dt);
    36→
    37→    if duration.num_seconds() < 60 {
    38→        "just now".to_string()
    39→    } else if duration.num_minutes() < 60 {
    40→        let mins = duration.num_minutes();
    41→        format!("{} minute{} ago", mins, if mins == 1 { "" } else { "s" })
    42→    } else if duration.num_hours() < 24 {
    43→        let hours = duration.num_hours();
    44→        format!("{} hour{} ago", hours, if hours == 1 { "" } else { "s" })
    45→    } else if duration.num_days() < 7 {
    46→        let days = duration.num_days();
    47→        if days == 1 {
    48→            "yesterday".to_string()
    49→        } else {
    50→            format!("{} days ago", days)
    51→        }
    52→    } else if duration.num_weeks() < 4 {
    53→        let weeks = duration.num_weeks();
    54→        format!("{} week{} ago", weeks, if weeks == 1 { "" } else { "s" })
    55→    } else {
    56→        // For older dates, show the actual date
    57→        dt.format("%b %d, %Y").to_string()
    58→    }
    59→}
    60→
    61→/// Render a single repository card
    62→#[allow(dead_code)]
    63→fn repository_card(event: &Event) -> Markup {
    64→    repository_card_with_bounty_count(event, &std::collections::HashMap::new())
    65→}
    66→
    67→fn repository_card_with_bounty_count(event: &Event, bounty_counts: &std::collections::HashMap<String, usize>) -> Markup {
    68→    let name = get_tag_value(event, "name").unwrap_or_else(|| "Unnamed Repository".to_string());
    69→    let description = get_tag_value(event, "description").unwrap_or_default();
    70→    let identifier = get_tag_value(event, "d").unwrap_or_default();
    71→    let has_clone_url = get_tag_value(event, "clone").is_some();
    72→    let has_web_url = get_tag_value(event, "web").is_some();
    73→    let bounty_count = bounty_counts.get(&identifier).copied().unwrap_or(0);
    74→
    75→    // Truncate pubkey for display
    76→    let short_pubkey = if event.pubkey.len() > 16 {
    77→        format!("{}...{}", &event.pubkey[..8], &event.pubkey[event.pubkey.len()-8..])
    78→    } else {
    79→        event.pubkey.clone()
    80→    };
    81→
    82→    html! {
    83→        a.repo-card href={"/repo/" (identifier)} {
    84→            div.repo-header {
    85→                h3.repo-name { (name) }
    86→                span.repo-id { "d:" (identifier) }
    87→            }
    88→            @if !description.is_empty() {
    89→                p.repo-description { (description) }
    90→            }
    91→            div.repo-meta {
    92→                span.repo-author { "by " (short_pubkey) }
    93→                @if has_clone_url {
    94→                    span.repo-clone { "Clone" }
    95→                }
    96→                @if has_web_url {
    97→                    span.repo-web { "View" }
    98→                }
    99→                @if bounty_count > 0 {
   100→                    span style="color: #fbbf24; margin-left: 0.5rem;" { "⚡ " (bounty_count) " bounties" }

