CREATE TABLE IF NOT EXISTS "issues" (
            id TEXT NOT NULL PRIMARY KEY,
            number INTEGER NOT NULL UNIQUE,
            title TEXT NOT NULL,
            description TEXT,
            status TEXT NOT NULL DEFAULT 'open',
            priority TEXT DEFAULT 'medium',
            issue_type TEXT DEFAULT 'task',
            is_blocked INTEGER DEFAULT 0,
            blocked_reason TEXT,
            claimed_by TEXT,
            claimed_at TEXT,
            created_at TEXT NOT NULL,
            updated_at TEXT NOT NULL,
            completed_at TEXT, agent TEXT NOT NULL DEFAULT 'claude', directive_id TEXT, project_id TEXT,
            CHECK(id != '')
        );
CREATE INDEX idx_issues_status ON issues(status);
CREATE INDEX idx_issues_number ON issues(number);
CREATE TRIGGER sync_issue_counter_on_insert
        AFTER INSERT ON issues
        BEGIN
            UPDATE issue_counter
            SET next_number = MAX(next_number, NEW.number + 1)
            WHERE id = 1;
        END;
CREATE INDEX idx_issues_directive ON issues(directive_id);
CREATE INDEX idx_issues_project ON issues(project_id);
CREATE INDEX idx_issues_agent ON issues(agent);
CREATE INDEX idx_issues_claimed_by ON issues(claimed_by);
