     1→//! Autopilot CLI subcommands
     2→//!
     3→//! Wraps autopilot crate CLI functions for unified binary.
     4→//! Note: The full autopilot CLI is extensive; this exposes core commands.
     5→
     6→use clap::Subcommand;
     7→use std::path::PathBuf;
     8→
     9→#[derive(Subcommand)]
    10→pub enum AutopilotCommands {
    11→    /// Run a task and log the trajectory
    12→    Run {
    13→        /// The task/prompt to execute
    14→        prompt: String,
    15→
    16→        /// Project name (loads cwd and issues_db from project)
    17→        #[arg(short, long)]
    18→        project: Option<String>,
    19→
    20→        /// Working directory (default: current directory or from project)
    21→        #[arg(short, long)]
    22→        cwd: Option<PathBuf>,
    23→
    24→        /// Model to use (sonnet, opus, haiku)
    25→        #[arg(short, long, default_value = "sonnet")]
    26→        model: String,
    27→
    28→        /// Enable issue tracking tools via MCP
    29→        #[arg(long)]
    30→        with_issues: bool,
    31→
    32→        /// Full auto mode: continuously work on issues
    33→        #[arg(long)]
    34→        full_auto: bool,
    35→    },
    36→
    37→    /// Resume a previous session
    38→    Resume {
    39→        /// Path to .json or .rlog trajectory file
    40→        trajectory: Option<PathBuf>,
    41→
    42→        /// Continue most recent session
    43→        #[arg(long, short = 'c')]
    44→        continue_last: bool,
    45→
    46→        /// Additional prompt to send on resume
    47→        #[arg(short, long)]
    48→        prompt: Option<String>,
    49→    },
    50→
    51→    /// Issue management
    52→    #[command(subcommand)]
    53→    Issue(IssueCommands),
    54→
    55→    /// View and analyze metrics
    56→    #[command(subcommand)]
    57→    Metrics(MetricsCommands),
    58→
    59→    /// Run benchmarks
    60→    Benchmark {
    61→        /// Specific benchmark to run
    62→        benchmark_id: Option<String>,
    63→
    64→        /// Category to run
    65→        #[arg(short, long)]
    66→        category: Option<String>,
    67→    },
    68→
    69→    /// Start the dashboard web UI
    70→    Dashboard {
    71→        /// Port to listen on
    72→        #[arg(short, long, default_value = "3000")]
    73→        port: u16,
    74→    },
    75→
    76→    /// Replay a saved trajectory
    77→    Replay {
    78→        /// Path to trajectory file
    79→        trajectory: PathBuf,
    80→    },
    81→
    82→    /// View APM (Actions Per Minute) statistics
    83→    #[command(subcommand)]
    84→    Apm(ApmCommands),
    85→}
    86→
    87→#[derive(Subcommand)]
    88→pub enum IssueCommands {
    89→    /// List issues
    90→    List {
    91→        /// Filter by status
    92→        #[arg(short, long)]
    93→        status: Option<String>,
    94→    },
    95→    /// Create a new issue
    96→    Create {
    97→        /// Issue title
    98→        title: String,
    99→        /// Issue body
   100→        #[arg(short, long)]
   101→        body: Option<String>,
   102→    },
   103→    /// Show issue details
   104→    Show {
   105→        /// Issue number
   106→        number: i32,
   107→    },
   108→    /// Claim an issue for work
   109→    Claim {
   110→        /// Issue number
   111→        number: i32,
   112→    },
   113→    /// Mark issue as complete
   114→    Complete {
   115→        /// Issue number
   116→        number: i32,
