   120→#[derive(Subcommand)]
   121→pub enum MetricsCommands {
   122→    /// Show metrics summary
   123→    Show,
   124→    /// Import metrics from trajectories
   125→    Import {
   126→        /// Path to trajectory directory
   127→        path: PathBuf,
   128→    },
   129→    /// Analyze metrics trends
   130→    Analyze,
   131→}
