   133→#[derive(Subcommand)]
   134→pub enum ApmCommands {
   135→    /// Show APM statistics for different time windows
   136→    Stats {
   137→        /// Source to display (autopilot, claude_code, combined)
   138→        #[arg(short, long)]
   139→        source: Option<String>,
   140→    },
   141→    /// List APM sessions
   142→    Sessions {
   143→        /// Source filter (autopilot, claude_code)
   144→        #[arg(short, long)]
   145→        source: Option<String>,
   146→        /// Limit number of results
   147→        #[arg(short, long, default_value_t = 20)]
   148→        limit: usize,
   149→    },
   150→}
   151→
   152→pub fn run(cmd: AutopilotCommands) -> anyhow::Result<()> {
   153→    // The autopilot CLI logic is currently embedded in main.rs.
   154→    // For the unified binary, we print a message directing to the legacy binary
   155→    // until we refactor autopilot to expose its CLI as a library.
   156→    match cmd {
   157→        AutopilotCommands::Dashboard { port } => {
   158→            let runtime = tokio::runtime::Runtime::new()?;
   159→            runtime.block_on(async {
   160→                // Use workspace root db path
   161→                let db_path = autopilot::default_db_path();
   162→                let db_str = db_path.to_string_lossy();
   163→                autopilot::dashboard::start_dashboard(&db_str, port).await
   164→            })
   165→        }
   166→        AutopilotCommands::Replay { trajectory } => {
   167→            // Load trajectory first, then replay
   168→            let traj = autopilot::replay::load_trajectory(&trajectory)?;
   169→            autopilot::replay::interactive_replay(&traj)
   170→        }
   171→        AutopilotCommands::Apm(apm_cmd) => {
   172→            use rusqlite::Connection;
   173→            use autopilot::apm_storage::{init_apm_tables, get_latest_snapshot, get_sessions_by_source};
   174→            use autopilot::apm::{APMSource, APMTier, APMWindow};
   175→            use colored::Colorize;
   176→
   177→            let db_path = autopilot::default_db_path();
   178→            let conn = Connection::open(&db_path)?;
   179→            init_apm_tables(&conn)?;
   180→
   181→            match apm_cmd {
   182→                ApmCommands::Stats { source } => {
   183→                    let source_filter = source.as_deref().map(|s| match s {
   184→                        "autopilot" => APMSource::Autopilot,
   185→                        "claude_code" | "claude" => APMSource::ClaudeCode,
   186→                        "combined" => APMSource::Combined,
   187→                        _ => {
   188→                            eprintln!("Invalid source: {}. Valid values: autopilot, claude_code", s);
   189→                            std::process::exit(1);
   190→                        }
   191→                    });
   192→
   193→                    println!("{}", "APM Statistics".cyan().bold());
   194→                    println!("{}", "─".repeat(70).dimmed());
   195→                    println!();
   196→
   197→                    let sources = if let Some(s) = source_filter {
   198→                        vec![s]
   199→                    } else {
   200→                        vec![APMSource::Autopilot, APMSource::ClaudeCode]
   201→                    };
   202→
   203→                    for src in sources {
   204→                        let latest = get_latest_snapshot(&conn, src, APMWindow::Lifetime)?;
   205→
   206→                        if let Some(snap) = latest {
   207→                            let tier = APMTier::from_apm(snap.apm);
   208→                            println!(
   209→                                "{:<15} {:>8.1} APM  ({}) - {} messages, {} tool calls",
   210→                                format!("{:?}", src).green().bold(),
   211→                                snap.apm,
   212→                                tier.name().yellow(),
   213→                                snap.messages,
   214→                                snap.tool_calls
   215→                            );
   216→                        } else {
   217→                            println!("{:<15} {}", format!("{:?}", src).dimmed(), "No data".dimmed());
   218→                        }
   219→                    }
   220→
   221→                    println!();
   222→                    Ok(())
   223→                }
   224→                ApmCommands::Sessions { source, limit } => {
   225→                    let src = source.as_deref().map(|s| match s {
   226→                        "claude_code" => APMSource::ClaudeCode,
   227→                        _ => APMSource::Autopilot,
   228→                    }).unwrap_or(APMSource::Autopilot);
   229→
   230→                    let sessions = get_sessions_by_source(&conn, src)?;
   231→
   232→                    println!("{}", "APM Sessions".cyan().bold());
   233→                    println!("{}", "─".repeat(70).dimmed());
   234→                    println!();
   235→
   236→                    if sessions.is_empty() {
   237→                        println!("{}", "No sessions found".dimmed());
   238→                    } else {
   239→                        for (id, start_time, end_time) in sessions.iter().take(limit) {
   240→                            let status = if end_time.is_some() { "✓" } else { "•" };
   241→                            println!(
   242→                                "{} {:<20} {}",
   243→                                status.green(),
   244→                                &id[..id.len().min(20)],
   245→                                start_time.format("%Y-%m-%d %H:%M:%S")
   246→                            );
   247→                        }
   248→                        println!();
   249→                        println!("Showing {} of {} sessions", sessions.len().min(limit), sessions.len());
   250→                    }
   251→
   252→                    println!();
   253→                    Ok(())
   254→                }
   255→            }
   256→        }
   257→        _ => {
   258→            // For commands that need the full autopilot infrastructure,
   259→            // we need to refactor autopilot's main.rs to expose the logic.
   260→            anyhow::bail!("This command requires the full autopilot runtime. Use: cargo autopilot <command>");
   261→        }
   262→    }
   263→}
