     1→//! Daemon-specific metrics collection and monitoring
     2→//!
     3→//! Tracks daemon and worker behavior for performance tuning and debugging.
     4→
     5→use crate::daemon::state::{WorkerState, WorkerStatus};
     6→use chrono::{DateTime, Utc};
     7→use serde::{Deserialize, Serialize};
     8→use std::collections::VecDeque;
     9→use std::sync::{Arc, RwLock};
    10→use std::time::{Duration, Instant};
    11→
    12→/// Metrics collected for the daemon
    13→#[derive(Debug, Clone, Serialize, Deserialize)]
    14→pub struct DaemonMetrics {
    15→    /// When metrics collection started
    16→    pub started_at: DateTime<Utc>,
    17→    /// Total number of worker restarts
    18→    pub total_restarts: u64,
    19→    /// Restart reasons (crash, memory, manual)
    20→    pub restart_reasons: RestartReasons,
    21→    /// Current memory usage (MB)
    22→    pub current_memory_mb: f64,
    23→    /// Peak memory usage (MB)
    24→    pub peak_memory_mb: f64,
    25→    /// Worker uptime (seconds)
    26→    pub worker_uptime_seconds: f64,
    27→    /// Total daemon uptime (seconds)
    28→    pub daemon_uptime_seconds: f64,
    29→    /// Recent restart history (last 10)
    30→    pub recent_restarts: Vec<RestartEvent>,
    31→}
    32→
    33→/// Categorized restart reasons
    34→#[derive(Debug, Clone, Serialize, Deserialize, Default)]
    35→pub struct RestartReasons {
    36→    pub crash: u64,
    37→    pub memory_pressure: u64,
    38→    pub manual: u64,
    39→    pub timeout: u64,
    40→}
    41→
    42→/// Individual restart event
    43→#[derive(Debug, Clone, Serialize, Deserialize)]
    44→pub struct RestartEvent {
    45→    pub timestamp: DateTime<Utc>,
    46→    pub reason: String,
    47→    pub uptime_seconds: f64,
    48→    pub memory_mb: Option<f64>,
    49→}
    50→
    51→/// Metrics collector for daemon operations
    52→pub struct DaemonMetricsCollector {
    53→    started_at: DateTime<Utc>,
    54→    daemon_started: Instant,
    55→    restart_reasons: Arc<RwLock<RestartReasons>>,
    56→    recent_restarts: Arc<RwLock<VecDeque<RestartEvent>>>,
    57→    peak_memory_mb: Arc<RwLock<f64>>,
    58→}
    59→
    60→impl DaemonMetricsCollector {
    61→    /// Create a new metrics collector
    62→    pub fn new() -> Self {
    63→        Self {
    64→            started_at: Utc::now(),
    65→            daemon_started: Instant::now(),
    66→            restart_reasons: Arc::new(RwLock::new(RestartReasons::default())),
    67→            recent_restarts: Arc::new(RwLock::new(VecDeque::with_capacity(10))),
    68→            peak_memory_mb: Arc::new(RwLock::new(0.0)),
    69→        }
    70→    }
    71→
    72→    /// Record a worker restart
    73→    pub fn record_restart(&self, reason: &str, uptime: Duration, memory_mb: Option<f64>) {
    74→        let mut reasons = self.restart_reasons.write().unwrap();
    75→
    76→        // Categorize restart reason
    77→        if reason.contains("crash") || reason.contains("failed") {
    78→            reasons.crash += 1;
    79→        } else if reason.contains("memory") {
    80→            reasons.memory_pressure += 1;
    81→        } else if reason.contains("manual") || reason.contains("requested") {
    82→            reasons.manual += 1;
    83→        } else if reason.contains("timeout") || reason.contains("stall") {
    84→            reasons.timeout += 1;
    85→        } else {
    86→            reasons.crash += 1; // Default to crash
    87→        }
    88→
    89→        // Add to recent restarts
    90→        let event = RestartEvent {
    91→            timestamp: Utc::now(),
    92→            reason: reason.to_string(),
    93→            uptime_seconds: uptime.as_secs_f64(),
    94→            memory_mb,
    95→        };
    96→
    97→        let mut recent = self.recent_restarts.write().unwrap();
    98→        if recent.len() >= 10 {
    99→            recent.pop_front();
   100→        }
   101→        recent.push_back(event);
   102→    }
   103→
   104→    /// Update memory tracking
   105→    pub fn update_memory(&self, current_mb: f64) {
   106→        let mut peak = self.peak_memory_mb.write().unwrap();
   107→        if current_mb > *peak {
   108→            *peak = current_mb;
   109→        }
   110→    }
   111→
   112→    /// Get current metrics snapshot
   113→    pub fn snapshot(&self, worker_state: &WorkerState, current_memory_mb: f64) -> DaemonMetrics {
   114→        let reasons = self.restart_reasons.read().unwrap();
   115→        let recent = self.recent_restarts.read().unwrap();
   116→        let peak = *self.peak_memory_mb.read().unwrap();
   117→
   118→        let worker_uptime_seconds = match &worker_state.status {
   119→            WorkerStatus::Running { started_at, .. } => started_at.elapsed().as_secs_f64(),
   120→            _ => 0.0,
   121→        };
   122→
   123→        DaemonMetrics {
   124→            started_at: self.started_at,
   125→            total_restarts: worker_state.total_restarts,
   126→            restart_reasons: reasons.clone(),
   127→            current_memory_mb,
   128→            peak_memory_mb: peak,
   129→            worker_uptime_seconds,
   130→            daemon_uptime_seconds: self.daemon_started.elapsed().as_secs_f64(),
   131→            recent_restarts: recent.iter().cloned().collect(),
   132→        }
   133→    }
   134→
   135→    /// Get total restart count
   136→    pub fn total_restarts(&self) -> u64 {
   137→        let reasons = self.restart_reasons.read().unwrap();
   138→        reasons.crash + reasons.memory_pressure + reasons.manual + reasons.timeout
   139→    }
   140→}
   141→
   142→impl Default for DaemonMetricsCollector {
   143→    fn default() -> Self {
   144→        Self::new()
   145→    }
   146→}
   147→
   148→#[cfg(test)]
   149→mod tests {
   150→    use super::*;
   151→
   152→    #[test]
   153→    fn test_metrics_collector_creation() {
   154→        let collector = DaemonMetricsCollector::new();
   155→        assert_eq!(collector.total_restarts(), 0);
   156→    }
   157→
   158→    #[test]
   159→    fn test_record_restart_crash() {
   160→        let collector = DaemonMetricsCollector::new();
   161→
   162→        collector.record_restart("worker crash", Duration::from_secs(60), Some(512.0));
   163→
   164→        let reasons = collector.restart_reasons.read().unwrap();
   165→        assert_eq!(reasons.crash, 1);
   166→        assert_eq!(reasons.memory_pressure, 0);
   167→        assert_eq!(reasons.manual, 0);
   168→
   169→        let recent = collector.recent_restarts.read().unwrap();
   170→        assert_eq!(recent.len(), 1);
   171→        assert_eq!(recent[0].uptime_seconds, 60.0);
   172→        assert_eq!(recent[0].memory_mb, Some(512.0));
   173→    }
   174→
   175→    #[test]
   176→    fn test_record_restart_memory() {
   177→        let collector = DaemonMetricsCollector::new();
   178→
   179→        collector.record_restart("memory pressure", Duration::from_secs(120), Some(1024.0));
   180→
   181→        let reasons = collector.restart_reasons.read().unwrap();
   182→        assert_eq!(reasons.crash, 0);
   183→        assert_eq!(reasons.memory_pressure, 1);
   184→    }
   185→
   186→    #[test]
   187→    fn test_record_restart_manual() {
   188→        let collector = DaemonMetricsCollector::new();
   189→
   190→        collector.record_restart("manual restart requested", Duration::from_secs(300), None);
   191→
   192→        let reasons = collector.restart_reasons.read().unwrap();
   193→        assert_eq!(reasons.manual, 1);
   194→    }
   195→
   196→    #[test]
   197→    fn test_recent_restarts_limit() {
   198→        let collector = DaemonMetricsCollector::new();
   199→
   200→        // Add 15 restarts
   201→        for i in 0..15 {
   202→            collector.record_restart("crash", Duration::from_secs(60), Some(i as f64));
   203→        }
   204→
   205→        let recent = collector.recent_restarts.read().unwrap();
   206→        assert_eq!(recent.len(), 10); // Should only keep last 10
   207→
   208→        // Verify it's the most recent 10
   209→        assert_eq!(recent[0].memory_mb, Some(5.0)); // 6th restart
   210→        assert_eq!(recent[9].memory_mb, Some(14.0)); // 15th restart
   211→    }
   212→
   213→    #[test]
   214→    fn test_update_memory() {
   215→        let collector = DaemonMetricsCollector::new();
   216→
   217→        collector.update_memory(512.0);
   218→        assert_eq!(*collector.peak_memory_mb.read().unwrap(), 512.0);
   219→
   220→        collector.update_memory(256.0); // Lower - shouldn't update peak
   221→        assert_eq!(*collector.peak_memory_mb.read().unwrap(), 512.0);
   222→
   223→        collector.update_memory(1024.0); // Higher - should update peak
   224→        assert_eq!(*collector.peak_memory_mb.read().unwrap(), 1024.0);
   225→    }
   226→
   227→    #[test]
   228→    fn test_snapshot() {
   229→        let collector = DaemonMetricsCollector::new();
   230→        let worker_state = WorkerState::new();
   231→
   232→        collector.record_restart("crash", Duration::from_secs(60), Some(512.0));
   233→        collector.update_memory(768.0);
   234→
   235→        let snapshot = collector.snapshot(&worker_state, 512.0);
   236→
   237→        assert_eq!(snapshot.total_restarts, 0); // WorkerState hasn't been updated
   238→        assert_eq!(snapshot.restart_reasons.crash, 1);
   239→        assert_eq!(snapshot.current_memory_mb, 512.0);
   240→        assert_eq!(snapshot.peak_memory_mb, 768.0);
   241→        assert_eq!(snapshot.recent_restarts.len(), 1);
   242→    }
   243→}
